mapper.select:
SELECT small.id, small.large_category_item_id, small.title, small.due_date, small.assignee_id, small.body, small.status_id, small.amount, small.created_at, small.updated_at, small.is_deleted
FROM small_category_item small
JOIN large_category_item large ON small.large_category_item_id = large.id
JOIN checklist check ON large.checklist_id = check.id
WHERE check.id = #{checkListId}
AND large.id = #{largeCatItemId}
AND small.is_deleted = false;

SELECT small.id, small.large_category_item_id, small.title, small.due_date, small.assignee_id, small.body, small.status_id, small.amount, small.created_at, small.updated_at, small.is_deleted
  FROM small_category_item small
  JOIN large_category_item large ON small.large_category_item_id = large.id
  JOIN checklist check ON large.checklist_id = check.id
WHERE check.id = #{checkListId}
  AND large.id = #{largeCatItemId}
  AND small.id = #{smallCatItemId}
  AND small.is_deleted = false;

mapper.update:
UPDATE small_category_item
SET is_deleted = true, updated_at = NOW()
WHERE id = #{checkListId} AND large_category_item_id = #{largeCatItemId};
